import React, { useEffect, useState } from "react";
import "./SchoolContacts.css";
import SchoolTitle from "./SchoolTitle";
import ContactCard from "./ContactCard";
import { Fab } from "@mui/material";
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import { useSelector } from "react-redux";
import { selectOpenSchool } from "../features/schoolSlice";

function SchoolContacts() {
  const selectedSchool = useSelector(selectOpenSchool);
  const [pageId, setPageId] = useState(selectedSchool.schoolId);
  console.log(pageId);

  useEffect(() => {
    async function fetchData() {
      const oldId = selectedSchool.schoolId;
      const newId = oldId + 1;
      const response = await fetch(`http://localhost:5000/schools/1`);

      if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const school = await response.json();
      if (!school) {
        window.alert(`School with id ${newId} not found`)
        return;
      }
      console.log(school);
    }
    fetchData();
  }, [pageId])

  return (
    <div className="schoolContacts__container">

      <div className="schoolContacts__schoolName">
        <SchoolTitle name={selectedSchool.name} />
      </div>

      <div className="schoolContacts__contactGroup">
        {selectedSchool.contacts.map((contact, index) => (
          <ContactCard key={index} contact={contact} />
        ))}
      </div>

      <div className="schoolContacts__arrows">
        <div className="schoolContacts__arrowsPrevious">
          <Fab>
            <ArrowBackIosNewIcon />
          </Fab>
        </div>
        <div className="schoolContacts__arrowsNext">
          <Fab onClick={() => setPageId(pageId+1)}>
            <ArrowForwardIosIcon />
          </Fab>
        </div>
      </div>
    </div>
  );
}

export default SchoolContacts;
