import React from "react";
import "./ContactCard.css";
import EmailIcon from "@mui/icons-material/Email";
import { Fab } from "@mui/material";

function ContactCard({ contact }) {
  const selectedSchool = useSelector(selectOpenSchool);

  async function handleClick() {
    if (!contact.emailed && contact.emailAddress !== null) {
      window.open(`mailto:${contact.emailAddress}`);
    }

    const editedEmailed = {
      emailed: true
    };

    await fetch(`http://localhost:5000/update/${selectedSchool.schoolId}/${contact.id}`)

  };

  return (
    <div
      className="contactCard__container"
      style={{ opacity: contact.emailed && 0.4 }}
    >
      <div className="schoolContacts__contactCardLeft">
        <h2>{contact.name}</h2>
        <p>{contact.title}</p>
      </div>

      <div className="schoolContacts__contactCardRight">
        <Fab onClick={handleClick}>
          <EmailIcon />
        </Fab>
      </div>
    </div>
  );
}

export default ContactCard;
