import React, { useEffect, useState } from "react";
import SchoolEmailBlock from "./SchoolEmailBlock";
import SchoolName from "./SchoolName";
import "./SchoolRow.css";

function SchoolRow({ schoolName, contacts, schoolId, dbId }) {
  const [totalEmailed, setTotalEmailed] = useState();

  useEffect(() => {
    contacts.forEach(contact => {
      if (contact.emailed) {
        setTotalEmailed(totalEmailed+1)
      }
    })
    console.log(schoolName, totalEmailed);
  }, [])

  return (
    <div className="schoolRow__container">
      <SchoolName schoolName={schoolName} dbId={dbId} schoolId={schoolId} contacts={contacts} />
      {contacts.map((contact, index) => (
        <SchoolEmailBlock key={index} emailed={contact.emailed} />
      ))}
    </div>
  );
}

export default SchoolRow;
