import React, { useState } from 'react'
import "./SchoolEmailBlock.css";

function SchoolEmailBlock({ id, totalEmailed, totalResponses }) {
  const [blockColor, setBlockColor] = useState("white")
  
  useState(() => {
    if (id === 0) {
      console.log(id, totalEmailed, totalResponses);
    }
  }, [])


  return (
    <div className="schoolEmailBlock" style={{backgroundColor: (id > totalEmailed) ? "white" : (id < totalEmailed & id > totalResponses) ? "red" : (id < totalResponses) ? "green" : "none"}}></div>
  )
}

export default SchoolEmailBlock
