import React from "react";
import "./ContactCard.css";
import EmailIcon from "@mui/icons-material/Email";
import { Fab } from "@mui/material";
import { useEffect } from "react";
import { useState } from "react";

function ContactCard() {
  const [schools, sets] = useState([]);

  useEffect(() => {
    async function getSchools() {
      const response = await fetch("http://localhost:5000/schools");

      if (!response.ok) {
        const message = `An error occured: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const schools = await response.json();
      console.log(schools);
      setSchools(schools);
    }
    getSchools();
    return;
  }, [schools.length]);

  return (
    <div className="contactCard__container">
      <div className="schoolContacts__contactCardLeft">
        <h2>Mr. Matt Urbeck</h2>
        <p>Principal</p>
      </div>

      <div className="schoolContacts__contactCardRight">
        <Fab>
          <EmailIcon />
        </Fab>
      </div>
    </div>
  );
}

export default ContactCard;
