import React, { useEffect, useState } from 'react'
import "./SchoolList.css";
import SchoolRow from './SchoolRow';

function SchoolList() {
  const [schools, setSchools] = useState([]);

  useEffect(() => {
    async function getSchools() {
      const response = await fetch("http://localhost:5000/schools");

      if (!response.ok) {
        const message = `An error occured: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const schools = await response.json();
      console.log(schools);
      setSchools(schools);
    }
    getSchools();
    return;
  }, [schools.length]);

  return (
    <div className="schoolList__container">

    
    {/* get all school and map schools with id, contacts array */}
        <SchoolRow name="North High" />
        <SchoolRow name="Rockwood Summit High" />
        <SchoolRow name="Ozark High" />
        <SchoolRow name="Kickapoo High" />
        <SchoolRow name="Waynesville High" />
        <SchoolRow name="Otterville High" />
        <SchoolRow name="Buffalo High" />
        <SchoolRow name="Sparta High" />
        <SchoolRow name="North Mercer High" />
        <SchoolRow name="Braymer High" />
        <SchoolRow name="New Bloomfield High" />
        <SchoolRow name="Hillsboro High" />
        <SchoolRow name="Joplin High" />
        <SchoolRow name="Lockwood High" />
        <SchoolRow name="Miami High" />
        <SchoolRow name="Winona High" />
        <SchoolRow name="Fordland High" />
        <SchoolRow name="Cooter High" />
    </div>
  )
}

export default SchoolList
