import React, { useState } from 'react'
import "./SchoolEmailBlock.css";

function SchoolEmailBlock({ id, totalEmailed, totalResponses }) {
  const [blockColor, setBlockColor] = useState("white")

  useState(() => {
    if (id < totalEmailed & id < totalResponses) {
      setBlockColor("green")
    } else if (id < totalEmailed & id > totalResponses) {
      setBlockColor("red")
    }
  }, [])


  return (
    <div className="schoolEmailBlock" style={{backgroundColor: blockColor}}></div>
  )
}

export default SchoolEmailBlock
